function set_globals() {
    current_tile_id = 0;
    attempts = 0;
    current_word = "";
    is_input_blocked = false;
    answer = possible_answer_words[Math.floor(Math.random() * possible_answer_words.length)];
}

function init_game(board_width = word_length, board_height = max_attempts) {
    for (let i = 0; i < board_height * board_width; i++) {
        let tile = document.createElement('div');
        tile.classList.add(tile_class);
        tile.id = `tile${i}`;
        game_board.appendChild(tile);
        clear_tile(tile);
        tiles.push(tile);
    }
    set_globals();
}

function fill_tile(tile, text) {
    tile.textContent = text;
    tile.classList.add(used_tile_class);
}

function clear_tile(tile) {
    tile.textContent = '';
    tile.classList.remove(used_tile_class);
    tile.classList.remove(exact_letter_class);
    tile.classList.remove(has_letter_class);
    tile.classList.remove(no_letter_class);
}

function clear_keyboard() {
    available_letters.forEach(key => {
        keyboard_button = document.querySelector('#' + key);
        keyboard_button.classList.remove(exact_letter_class);
        keyboard_button.classList.remove(has_letter_class);
        keyboard_button.classList.remove(no_letter_class);
    })
}

function paint_keyboard_button(key, colorClass) {
    keyboard_button = document.querySelector('#' + key);
    keyboard_button.classList.add(colorClass);
}

function reset_game() {
    tiles.forEach(tile => {
        clear_tile(tile);
    });
    clear_keyboard();
    set_globals();
}

function evaluate_tile_layout() {
    let tile_layout = '';
    let tile_id = 0;
    for (let i = 0; i < attempts; i++) {
        let tile_layer = '';
        for (let j = 0; j < word_length; j++) {
            if (tiles[tile_id].classList.contains(exact_letter_class)) {
                tile_layer += '🟩';
            } else if (tiles[tile_id].classList.contains(has_letter_class)) {
                tile_layer += '🟨';
            } else {
                tile_layer += '⬜️';
            }
            tile_id++;
        }
        share_text += tile_layer + '\n';
        tile_layer += '<br>';
        tile_layout += tile_layer;
    }
    return tile_layout;
}

function showShareModal(score = null) {
    let win_text = share_modal.querySelector('.win-text');
    let lose_text = share_modal.querySelector('.lose-text');
    let tile_layout_text = share_modal.querySelector('.tile-layout');
    share_text = 'Я сыграл в Вордли\n\n';
    tile_layout_text.innerHTML = evaluate_tile_layout()
    share_text += '\n';
    let secret_word = share_modal.querySelector('.secret-word');
    secret_word.textContent = answer;
    share_text += share_modal.querySelector('.secret-word-text').textContent + '\n';
    if (score === null) {
        win_text.style.display = 'none';
        lose_text.style.display = 'block';
        share_text += "Я не угадал слово :(";
    } else {
        win_text.style.display = 'block';
        lose_text.style.display = 'none';
        let score_text = share_modal.querySelector('.score');
        score_text.textContent = score;
        share_text += win_text.textContent.replace('Вам', 'Мне');
    }
    share_text += '\n';
    share_text += 'Попробуй побить мой рекорд на @wordley_bot!';
    share_modal.classList.add('active');
    overlay.classList.add('active');
}

function hideShareModal() {
    share_modal.classList.remove('active');
    overlay.classList.remove('active');
}

function showGuideModal() {
    guide_modal.classList.add('active');
    overlay.classList.add('active');
    is_input_blocked = true;
}

