function show_win() {
    is_input_blocked = true;
    let score = current_tile_id / word_length;
    showShareModal(score);
}

function show_lose() {
    is_input_blocked = true;
    showShareModal();
}

function check_word_validity(checked_word) {
    if (available_words.includes(checked_word) || possible_answer_words.includes(checked_word)) {
        return true;
    }
    return false;
}

function show_word_similarity(main_word, checked_word) {
    let marked_letters = [];
    let marked_tiles = [];
    for (let i = 0; i < main_word.length; i++) {
        marked_letters.push(0), marked_tiles.push(0)
    }
    for (let i = 0; i < checked_word.length; i++) {
        let tile_id = current_tile_id - (checked_word.length) + i;
        if (checked_word[i] === main_word[i]) {
            tiles[tile_id].classList.add(exact_letter_class);
            paint_keyboard_button(checked_word[i], exact_letter_class);
            marked_letters[i] = 1;
            marked_tiles[i] = 1;
        }
    }
    for (let i = 0; i < main_word.length; i++) {
        if (marked_letters[i]) {
            continue;
        }
        if (!checked_word.includes(main_word[i])) {
            continue;
        }
        for (let j = 0; j < checked_word.length; j++) {
            if (checked_word[j] !== main_word[i]) {
                continue;
            }
            let tile_id = current_tile_id - (checked_word.length) + j;
            tiles[tile_id].classList.add(has_letter_class);
            paint_keyboard_button(checked_word[j], has_letter_class);
            marked_letters[i] = 1;
            marked_tiles[j] = 1;
            break;
        }
    }
    for (let i = 0; i < marked_tiles.length; i++) {
        if (marked_tiles[i] == 1) {
            continue;
        }
        let tile_id = current_tile_id - (checked_word.length) + i;
        tiles[tile_id].classList.add(no_letter_class);
        paint_keyboard_button(checked_word[i], no_letter_class);
    }
}

function pressKey(key) {
    if (!available_letters.includes(key)) {
        return;
    }
    if (key === 'backspace') {
        if (current_word.length == 0) {
            return;
        }
        is_input_blocked = false;
        current_tile_id--;
        clear_tile(tiles[current_tile_id]);
        current_word = current_word.slice(0, -1);
        return;
    }
    if (key === 'enter') {
        if (check_word_validity(current_word)) {
            attempts++;
            show_word_similarity(answer, current_word);
            if (current_word === answer) {
                show_win();
                return;
            } else if (current_tile_id == max_attempts * word_length) {
                show_lose();
                return;
            }
            is_input_blocked = false;
            current_word = "";
        }
        return;
    }
    if (is_input_blocked) {
        return;
    }
    fill_tile(tiles[current_tile_id], key);
    current_tile_id++;
    current_word += key;
    if (current_word.length == word_length) {
        is_input_blocked = true;
    }
}

document.addEventListener('keydown', (event) => {
    let key = event.key.toLowerCase();
    pressKey(key);
});

